mapper.select:
SELECT a.PLAN_ID, a.USER_ID, a.TITLE, a.CONTENT, a.DESTINATION, a.PERSONNEL, TO_CHAR(a.start_day, 'YY-MM-DD') as START_DAY, TO_CHAR(a.end_day, 'YY-MM-DD') as END_DAY, TO_CHAR(a.reg_date, 'YY-MM-DD') as REG_DATE, TO_CHAR(a.finish_date, 'YY-MM-DD') as FINISH_DATE, a.PLAN_STATE, REGEXP_SUBSTR(a.CONTENT, '<img[^>]+>', 1, 1) as first_image, b.nick, b.birth, b.gender FROM JOIN_BOARD_MEMBER c
JOIN JOIN_BOARD a ON a.PLAN_ID = c.PLAN_ID JOIN (SELECT nick, TRUNC((SYSDATE - BIRTH) / 365) as birth, DECODE(gender, 'M', '남자', 'F', '여자') as gender, user_id from USERS ) b ON b.user_id = a.USER_ID
WHERE c.USER_ID = #{userId}

select file_name
from join_board_image
where plan_id = #{planId}

mapper.update:

UPDATE join_board
SET views = views + 1 
WHERE plan_id = #{planId}

mapper.select:
SELECT *
FROM join_mark
WHERE plan_id = #{planId}
AND user_id = #{userId}
AND sort = #{sort}

mapper.update:
UPDATE join_board
SET likes = (SELECT count(*) FROM join_mark WHERE plan_id = #{planId} AND sort = 'L')
WHERE plan_id = #{planId}

mapper.select:
SELECT count(*) likes FROM join_mark
WHERE plan_id = #{planId}
AND sort = #{sort}
