mapper.update:
UPDATE join_board
SET PERSONNEL = PERSONNEL +1
WHERE plan_id = #{planId}

UPDATE join_board
SET plan_state = 1
WHERE plan_id = #{planId}

UPDATE join_board
SET plan_state = 2
WHERE plan_id = #{planId}

mapper.select:
SELECT plan_id, user_id
FROM JOIN_BOARD_MEMBER
WHERE plan_id = #{planId}

select COUNT(*) as here
from JOIN_BOARD_MEMBER
where user_id = #{userId}
and plan_id = #{planId}
